import React from 'react'
import LaptopImage from "../Images/hero_setup.webp";

function Welcome() {
  return (
    <section className="bg-[#0d071d] text-white py-20 px-4 md:px-16">
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between gap-10">
        {/* Text Section */}
        <div className="flex-1 text-center md:text-left">
          <h1 className="text-5xl md:text-6xl font-light mb-6">Welcome.</h1>
          <p className="text-lg leading-relaxed max-w-xl mx-auto md:mx-0">
            My name is Ian Dunkerley, a front-end developer based in Torquay,
            Devon, UK. I have worked on a wide range of front-end projects, from
            DJ applications to eCommerce booking platforms, with a focus on
            creating clean, well-crafted interfaces that not only look great but
            also provide a seamless user experience.
          </p>
        </div>

        {/* Image Section */}
        <div className="flex-1 flex justify-center md:justify-end">
          <img
            src={LaptopImage}
            alt="Laptop with code"
            className="w-80 md:w-[400px] rounded-xl shadow-xl"
          />
        </div>
      </div>

      {/* Bottom Wave (optional) */}

    </section>
  );
}

export default Welcome
