import React from "react";
import Navbar from "./components/Navbar";
import About from "./components/About";
import Skills from "./components/Skills";
import Projects from "./components/Projects";
import Experience from "./components/Experience";
import Education from "./components/Education";
import Contact from "./components/Contact";
import Footer from "./components/Footer";
import Welcome from "./components/Welcome";

const App = () => {
  return (
    <div className="bg-gradient-to-br from-blue-50 to-purple-100 min-h-screen text-gray-800 scroll-smooth">
      <div className=" absolute bottom-0 left-0 w-full overflow-hidden leading-[0]">
        <svg
          className="relative block w-full h-[60px] md:h-[100px] "
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 1440 320"
          preserveAspectRatio="none"
        >
          <defs>
            <linearGradient id="waveGradient" x1="0" y1="0" x2="1" y2="1">
              <stop offset="0%" stopColor="#eff6ff" /> {/* from-blue-50 */}
              <stop offset="100%" stopColor="#e9d5ff" /> {/* to-purple-100 */}
            </linearGradient>
          </defs>
          <path
            fill="url(#waveGradient)"
            d="M0,224L60,229.3C120,235,240,245,360,240C480,235,600,213,720,202.7C840,192,960,192,1080,213.3C1200,235,1320,277,1380,298.7L1440,320L1440,320L0,320Z"
          ></path>
        </svg>
      </div>
      <Navbar />
      <main className="">
        <Welcome />
        <About />
        <Skills />
        <Experience />
        <Projects />
        <Education />
        <Contact />
        <Footer />
      </main>
    </div>
  );
};

export default App;
