import React from "react";
import {
  FaProjectDiagram,
  FaShoppingCart,
  FaShieldAlt,
  FaRocket,
  FaSearch,
  FaServer,
  FaCloud,
  FaCodeBranch,
} from "react-icons/fa";
import {
  SiMongodb,
  SiExpress,
  SiReact,
  SiRedux,
  SiNodeDotJs,
  SiRazorpay,
  SiGoogleanalytics,
} from "react-icons/si";
import {
  BsDatabaseGear,
  BsDiagram3,
  BsStack,
  BsGraphUpArrow,
  BsCloudCheck,
} from "react-icons/bs";

const techBadge = (icon, label) => (
  <div className="flex items-center space-x-1 bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs font-semibold">
    {icon}
    <span>{label}</span>
  </div>
);

const Projects = () => {
  return (
    <section id="projects" className="mt-16">
      <h2 className="text-3xl font-bold mb-6 text-blue-700 flex items-center">
        <FaProjectDiagram className="mr-2 text-yellow-500" />
        Projects
      </h2>

      {/* Project 1 */}
      <div className="mb-10 p-6 rounded-lg bg-white shadow-md hover:shadow-xl transition duration-300">
        <h3 className="text-xl font-semibold mb-2 text-gray-800">
          Official Website / Web Portal – Proneta Tech Pvt. Ltd.
        </h3>
        <ul className="list-disc list-inside text-sm text-gray-600 space-y-2">
          <li>
            Designed and developed a full-stack MERN application using{" "}
            <strong>MongoDB</strong>, <strong>Express.js</strong>,{" "}
            <strong>React.js</strong>, and <strong>Node.js</strong>.
          </li>
          <li>
            Created robust REST APIs with Express and Node for data processing
            and CRUD operations.
          </li>
          <li>
            Used <strong>MongoDB Aggregation Framework</strong> and optimized
            indexes for performance.
          </li>
          <li>
            Built dynamic UI with component architecture, hooks, and{" "}
            <strong>Redux</strong> for state management.
          </li>
          <li>
            Integrated <strong>JWT authentication</strong> and session handling.
          </li>
          <li>
            Implemented persistent cart using{" "}
            <strong>local/session storage</strong>.
          </li>
          <li>
            Added <strong>Razorpay</strong> payment gateway and webhook
            handling.
          </li>
          <li>
            Configured <strong>Google Analytics</strong> UTM tracking for source
            and conversion monitoring.
          </li>
          <li>
            Migrated PHP site to MERN with <strong>SEO-friendly URLs</strong>.
          </li>
          <li>
            Deployed to cloud, managed domains, and improved response time with{" "}
            <strong>lazy loading</strong>.
          </li>
        </ul>
        <div className="flex flex-wrap gap-2 mt-4">
          {techBadge(<SiMongodb />, "MongoDB")}
          {techBadge(<SiExpress />, "Express.js")}
          {techBadge(<SiReact />, "React.js")}
          {techBadge(<SiNodeDotJs />, "Node.js")}
          {techBadge(<SiRedux />, "Redux")}
          {techBadge(<SiRazorpay />, "Razorpay")}
          {techBadge(<SiGoogleanalytics />, "Google Analytics")}
          {techBadge(<FaShieldAlt />, "JWT Auth")}
          {techBadge(<FaShoppingCart />, "Persistent Cart")}
          {techBadge(<BsDatabaseGear />, "Aggregation")}
          {techBadge(<FaSearch />, "SEO Friendly")}
          {techBadge(<FaCloud />, "Cloud Deployment")}
        </div>
      </div>

      {/* Project 2 */}
      <div className="mb-10 p-6 rounded-lg bg-white shadow-md hover:shadow-xl transition duration-300">
        <h3 className="text-xl font-semibold mb-2 text-gray-800">
          EMS – Election Management System
        </h3>
        <ul className="list-disc list-inside text-sm text-gray-600 space-y-2">
          <li>
            Developed a full-featured EMS platform with the MERN stack to manage
            elections at the constituency level.
          </li>
          <li>
            Designed complex database schema using{" "}
            <strong>MongoDB aggregation</strong>, <strong>$lookup</strong>, and
            indexing for analytics.
          </li>
          <li>
            Implemented <strong>dynamic dashboards</strong> and real-time data
            visualizations for election tracking.
          </li>
          <li>
            Enabled <strong>RBAC</strong> for candidates, admins, and officials.
          </li>
          <li>
            Secured sessions using <strong>JWT</strong> and integrated Google
            UTM tracking.
          </li>
          <li>
            Optimized frontend with lazy loading and backend with efficient API
            design.
          </li>
          <li>
            Deployed on scalable cloud infrastructure for high availability.
          </li>
        </ul>
        <div className="flex flex-wrap gap-2 mt-4">
          {techBadge(<SiMongodb />, "MongoDB")}
          {techBadge(<SiExpress />, "Express.js")}
          {techBadge(<SiReact />, "React.js")}
          {techBadge(<SiNodeDotJs />, "Node.js")}
          {techBadge(<FaShieldAlt />, "JWT Auth")}
          {techBadge(<BsDiagram3 />, "RBAC")}
          {techBadge(<BsCloudCheck />, "Cloud Hosted")}
          {techBadge(<SiGoogleanalytics />, "Analytics + UTM")}
          {techBadge(<FaRocket />, "Real-time Dashboard")}
          {techBadge(<FaServer />, "Optimized API")}
        </div>
      </div>
    </section>
  );
};

export default Projects;
