import React from "react";

const skillBadge = (svgIcon, label, color) => (
  <div
    className={`flex items-center space-x-2 px-3 py-2 rounded shadow-sm text-white text-sm font-medium ${color}`}
  >
    <span className="w-5 h-5" dangerouslySetInnerHTML={{ __html: svgIcon }} />
    <span>{label}</span>
  </div>
);

// Bootstrap SVG icons
const icons = {
  react: `<svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" class="bi bi-lightning-charge" viewBox="0 0 16 16"><path d="M11.251.13a.5.5 0 0 1 .46.669L10.229 5H14a.5.5 0 0 1 .39.812l-9 11a.5.5 0 0 1-.884-.47L5.771 11H2a.5.5 0 0 1-.39-.812l9-11a.5.5 0 0 1 .641-.058ZM3.14 10h3.354a.5.5 0 0 1 .49.598l-.647 3.89L12.86 6h-3.354a.5.5 0 0 1-.49-.598l.647-3.89L3.14 10Z"/></svg>`,
  db: `<svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" class="bi bi-database" viewBox="0 0 16 16"><path d="M4.5 0a.5.5 0 0 0-.5.5v1.088A6.488 6.488 0 0 0 2.44 3.56 6.486 6.486 0 0 0 1.5 6c0 1.603.614 3.067 1.62 4.18A6.494 6.494 0 0 0 4 12.743V15.5a.5.5 0 0 0 .5.5h7a.5.5 0 0 0 .5-.5v-2.757a6.494 6.494 0 0 0 .88-.563 6.486 6.486 0 0 0 1.62-4.18c0-1.603-.614-3.067-1.62-4.18A6.488 6.488 0 0 0 11.5 1.588V.5a.5.5 0 0 0-.5-.5h-6Z"/></svg>`,
  css: `<svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" class="bi bi-palette-fill" viewBox="0 0 16 16"><path d="M0 8a8 8 0 1 1 13.623 6.193c-.417.305-.887.531-1.391.66a3 3 0 1 1-5.308-.223 3.006 3.006 0 0 1-.607.07A3 3 0 1 1 3 10.215 2.996 2.996 0 0 1 3 8.5a2.99 2.99 0 0 1 1.309-2.489A3.006 3.006 0 0 1 5.5 3a3 3 0 0 1 2.646 1.536c.346-.112.714-.19 1.096-.228A2.5 2.5 0 0 1 13.5 6c0 1.08-.565 2.048-1.438 2.595.076.115.15.233.22.355A3 3 0 1 1 8 15a2.993 2.993 0 0 1-.393-.026 8.034 8.034 0 0 1-3.826-1.222A8.035 8.035 0 0 1 0 8Z"/></svg>`,
  auth: `<svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" class="bi bi-shield-lock" viewBox="0 0 16 16"><path d="M5.5 9.995V9a2.5 2.5 0 1 1 5 0v.995c.57.094 1 .572 1 1.164v1.5a1.5 1.5 0 0 1-3 0v-1.5c0-.592.43-1.07 1-1.164V9a1.5 1.5 0 0 0-3 0v.159a1.5 1.5 0 0 0-.5-.086 1.5 1.5 0 0 0-.5.086Zm2.5 1.664v1.5a.5.5 0 0 0 1 0v-1.5a.5.5 0 0 0-1 0Z"/><path d="M7.45.661a1 1 0 0 1 1.1 0l6 4A1 1 0 0 1 15 5.5v5a8.5 8.5 0 0 1-7 8.37A8.5 8.5 0 0 1 1 10.5v-5a1 1 0 0 1 .45-.839l6-4ZM8 1.933 2 5.5v5a7.5 7.5 0 0 0 6 7.421A7.5 7.5 0 0 0 14 10.5v-5L8 1.933Z"/></svg>`,
  tools: `<svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" class="bi bi-tools" viewBox="0 0 16 16"><path d="M1 0a1 1 0 0 0-1 1v6h1V1h6V0H1Zm9.646 2.146a.5.5 0 0 1 .708 0l2.5 2.5a.5.5 0 0 1 0 .708l-1.528 1.528a3.5 3.5 0 1 1-.707-.707l1.528-1.528a.5.5 0 0 1 .708 0ZM6 7a3 3 0 0 1 3-3 2.99 2.99 0 0 1 2.165.949L6.95 9.165A2.99 2.99 0 0 1 6 7Zm3 5.5a.5.5 0 0 1 .5.5v1h5a.5.5 0 0 1 0 1h-5v1a.5.5 0 0 1-1 0v-1H4.707l1.647-1.646a.5.5 0 0 1 .707.707L5.414 14H9v-1a.5.5 0 0 1 .5-.5Z"/></svg>`,
};

const Skills = () => {
  return (
    <section id="skills" className="mt-12">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">Skills</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3">
        {skillBadge(
          icons.react,
          "React.js, Next.js, Node.js, Express.js",
          "bg-blue-600"
        )}
        {skillBadge(icons.db, "MongoDB, MySQL", "bg-green-600")}
        {skillBadge(icons.css, "Tailwind CSS, Bootstrap", "bg-pink-600")}
        {skillBadge(icons.auth, "JWT Auth, REST APIs", "bg-indigo-600")}
        {skillBadge(icons.cloud, "AWS, Docker, Git, GitHub", "bg-gray-700")}
        {skillBadge(icons.tools, "Google Analytics, Postman", "bg-yellow-600")}
      </div>
    </section>
  );
};

export default Skills;
